import React, { useState, useEffect, } from "react";
import { useNavigate } from "react-router-dom"
import axios from "axios"
import { usePlaidLink } from 'react-plaid-link';



export const SignupForm = ({ register }) => {
    const navigate = useNavigate()
    const [tempToken, setTempToken] = useState();
    const [publicToken, setPublicToken] = useState();
    const [acessToken, setAccessToken] = useState(); 


    // const history = useHistory();
    const form = {
        username: "",
        password: "",
        firstName: "",
        lastName: "",
    };


    const [signup, setSignup] = useState(form);
    async function handleSubmit(e) {

        e.preventDefault();
        register(signup.username, signup.password, signup.firstName, signup.lastName);
        navigate("/");

    }


    async function createLinkToken() {
        const response = await axios.post("/create_link_token")
        console.log(response);
        setTempToken(response.data.link_token);
    };
    async function access_token() {
        const accessToken = await axios.post("/exchange_public_token", { publicToken: publicToken })
        setAccessToken(accessToken.data.accessToken)
        console.log("access token: ", accessToken.data);
    }

    const { open, ready } = usePlaidLink({
        token: tempToken,
        onSuccess: (public_token, metadata) => {
            setPublicToken(public_token);
            console.log("success!", public_token, metadata)
            // send public_token to server
        },
    });







    const handleChange = (e) => {
        const { name, value } = e.target;
        setSignup(prevState => ({
            ...prevState,
            [name]: value
        }));


    };



    return (
        <>
            <h1>Signup</h1>

            {console.log(signup)}

            <form onSubmit={handleSubmit}>
                {

                }
                <label htmlFor="username"><b>Username</b></label>
                <input
                    name="username"
                    type="text"
                    onChange={handleChange}
                    value={signup.username}
                />

                <label htmlFor="password"><b>Password</b></label>
                <input
                    name="password"
                    type="text"
                    onChange={handleChange}
                    value={signup.password}
                />

                <label htmlFor="firstName"><b>First name</b></label>
                <input
                    name="firstName"
                    type="text"
                    onChange={handleChange}
                    value={signup.firstName}
                />

                <label htmlFor="lastName"><b>Last name</b></label>
                <input
                    name="lastName"
                    type="text"
                    onChange={handleChange}
                    value={signup.lastName}
                />

                <button onClick={() => open()} disabled={!ready}>
                    Connect a bank account
                </button>

                <br />
                <button> Submit </button>
            </form>


        </>
    )
}
