import React, { useContext, useEffect, useState } from "react";
import { UserContext } from "./userContext";
import { Api } from "./Api";

export const InVOut = () => {
    const { currentUser, balances } = useContext(UserContext);
    const [totalTransactions, setTotalTransactions] = useState(null);

    useEffect(() => {
        const fetchBalance = async () => {
            try {
                // Calculate start and end dates for the current month
                const currentDate = new Date();
                const startDate = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).toISOString().split('T')[0];
                const endDate = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).toISOString().split('T')[0];

                const transactions = await Api.getTransactions(startDate, endDate, ); // Update API call with start and end dates
                console.log("______", transactions)
                const validTransactions = transactions.filter(t =>
                    parseFloat(t.amount) > 0 &&
                    t.category.toLowerCase().indexOf('deposit') === -1 &&
                    t.category.toLowerCase().indexOf('transfer') === -1
                );
                const total = validTransactions.reduce((acc, t) => acc + parseFloat(t.amount), 0);
                setTotalTransactions(total.toFixed(2));
            } catch (error) {
                console.error("Error fetching transactions:", error);
            }
        };

        fetchBalance();
    }, []);

    if (!balances) {
        return <div>Loading</div>;
    }

    const checkingBalance = balances.filter(account => account.name.includes("CHECKING"));

    return (
        <div>
            <h1>Income vs transactions ðŸ’¸</h1>
            <p>Checking Balance: {checkingBalance[0].balance}</p>
            <p>Total Transactions: {totalTransactions !== null ? totalTransactions : 'Loading...'}</p>
        </div>
    );
};
