import React, {useState, useEffect} from "react";
import axios from "axios"
import { usePlaidLink } from 'react-plaid-link';
import { Api } from "./Api";
axios.defaults.baseURL = 'http://localhost3001';

export const ConnectBank = ({temp}) => {
    const [linkToken, setLinkToken] = useState();
    const [publicToken, setPublicToken] = useState();



    useEffect(() => {
        async function token_link() {
            const tl = await Api.getLinkToken();
            console.log(tl)
            setLinkToken(tl);
        }
        token_link();  
    }, []);

 const { open, ready } = usePlaidLink({
        token: linkToken,
        onSuccess: (public_token, metadata) => {
            setPublicToken(public_token);
            console.log("success!", public_token, metadata)
            // send public_token to server
        },
    });

    useEffect(() =>
    {
          async function access_token() {
        const accessToken = await axios.post("/exchange_public_token", { publicToken: publicToken })
        console.log("access token:", accessToken);
    }
        access_token();
   },[publicToken])



    return (
        <div>

            <button onClick={() => open()} disabled={!ready}>
                Connect a bank account
            </button>
        </div>
    )
}
